import React, { useState } from "react";
import { ContainerMessenger } from "./style";

function Messenger() {

    // Estados
    const [inputNome, setInputNome] = useState("");
    const [inputMensagem, setInputMensagem] = useState("");
    const [mensagens, setMensagens] = useState([])
    
    const ListaDeMensagens = mensagens.map((mensagem) => {
        return (<p key={mensagem}>{mensagem}</p>)
    })

    // Eventos
    const handleInputNome = (event) => {
        setInputNome(event.target.value)
    }

    const handleInputMensagem = (event) => {
        setInputMensagem(event.target.value)
    }

    // Adicionar Mensagem
    const adicionarMensagem =(event) => {
        event.preventDefault();
        
        const novaMensagem = [...mensagens]
        novaMensagem.push()
        setMensagens(adicionarMensagem)
    }

    const enviarMensagem = (event) => {
        event.preventDefault();

        const novaMensagem = { usuario: inputNome, mensagem: inputMensagem }
        const novaListaDeMensagens = [...mensagens, novaMensagem]
        setMensagens(novaListaDeMensagens)
    }


    return (
        <ContainerMessenger>
            <form>
            {enviarMensagem}
            {ListaDeMensagens}
                <input name="usuario" placeholder="UsuÃ¡rio" value={inputNome} onChange={handleInputNome} />
                <input name="mensagem" placeholder="Escreva uma mensagem" value={inputMensagem} onChange={handleInputMensagem} />
                <button onClick={adicionarMensagem} >Enviar</button>
            </form>
        </ContainerMessenger>
    )
}

export default Messenger;
