import styled from "styled-components";


export const ContainerMessenger = styled.section`
display: flex;
height: 100vh;
border: 1px solid grey;
width: 70vw;
margin: 0 auto;
`

export const SubContainer = styled.section`
flex-wrap: wrap;
display: flex;
position: fixed;
bottom: 10px;

`
