import styled from "styled-components";


export const ContainerMessenger = styled.section`
display: flex;
height: 100vh;
border: 1px solid grey;
width: 70vw;
margin: 0 auto;
word-break: break-all;
`

export const SubContainer = styled.section`
position: fixed;
bottom: 10px;
`

export const Balao = styled.div`
background-color: red;
border: 1px solid;
border-radius: 10;
justify-content: center;
text-align: justify;
width: 40vw;
margin: 0 auto;
`
