import styled from "styled-components";


export const Container = styled.fieldset`
display: flex;
flex-direction: column;
background-color: orange;
width:100vw;
height: 50vh;
margin: 0 auto;
border: none;
`

export const Formulario = styled.form`
display: flex;
flex-direction: column;
width: 50vw;
margin: 2em auto;
position: fixed;
`
