import React from "react";
import Etapa1 from "./components/PrimeiraEtapa";
import SegundaEtapa from "./components/SegundaEtapa";
import TerceiraEtapa from "./components/TerceiraEtapa"
import EtapaFinal from "./components/EtapaFinal";

function App(){
  state = {
    etapa: 1
  }
  return(

  pulaEtapa = () => {
    switch (this.state.etapa) {
      case 1:
        return <Etapa1 />;
        break;
      case 2:
        return <SegundaEtapa />;
        break;
      case 3:
        return <TerceiraEtapa />;
        break;
      case 4:
        return <EtapaFinal />;
        break;
      default:
        break;
      };
    })
  }
  
  const pulaEtapa =() => {
    return pulaEtapa;
  }
   pulaEtapa = (value) => {
    pulaEtapa = value;
  }

  add = () => {
    setState({ etapa: this.state.etapa + 1 })
    
    return (
      <>
        {this.pulaEtapa()}
        {this.state.etapa !== 4 && (
          <button onClick={this.add}>Pr√≥xima etapa</button>
        )}
      </>

)
}
  


export default App;
