/* eslint-disable no-unreachable */
import React, { useState } from "react";
import Etapa1 from "./components/PrimeiraEtapa";
import SegundaEtapa from "./components/SegundaEtapa";
import TerceiraEtapa from "./components/TerceiraEtapa"
import EtapaFinal from "./components/EtapaFinal";


function App() {

  const [etapa, setEtapa] = useState('')
  state = {
    etapa: 1,
  }

  const renderizaEtapa = () => {
    switch (tetapa) {
      case 1:
        return <Etapa1 />;
        break;
      case 2:
        return <SegundaEtapa />;
        break;
      case 3:
        return <TerceiraEtapa />;
        break;
      case 4:
        return <EtapaFinal />;
        break;
      default:
        break;
    }
  }

  const proximaEtapa =()=>{
    const etapaAtual = this.state.etapa;
    const etapaSeguinte = etapaAtual +1;
    this.setState({etapa: etapaSeguinte});
  }

  return (
    <div>
      <Etapa1 />
      <SegundaEtapa />
      <TerceiraEtapa />
      <EtapaFinal />
      <button onClick={proximaEtapa}>Pr√≥ximo</button>
      {renderizaEtapa}
    </div>
  );
}

export default App;
