import styled from "styled-components";

export const DivPai=styled.div`
background-color:lightgreen;
height: 90vh;
display: flex;
align-items: center;
justify-content:space-between;
`

export const Colunas=styled.span`
display:flex;
background-color: orange;
height: 90vh;
width: 10vw;
overflow: hidden;
`
export const Input=styled.section`
display: flex;
flex-direction: row;
flex-wrap: nowrap;
justify-content:flex-start;
font-size: 1.2rem;
width: 100%;
margin-left: 10px;
margin-bottom: 300px;
`
