justify-content: center;
text-align: center;
padding: 0 90px;
height: 1vh;
top:40px;
`
export const TituloPrincipal= styled.h1`
/* display: flex; */
text-align: center;
align-items: center;
justify-content: center;
/* top: 0; */
color:#070809;
font-size: 2.3rem;
font-weight: 700;
`
