import styled from "styled-components";

export const DivPai=styled.div`
background-color:lightgreen;
height: 90vh;
display: flex;
`

export const Colunas=styled.span`
display:flex;
background-color: orange;
height: 90vh;
width: 10vw;
`
export const Input=styled.section`
display: flex;
font-size: 1.2rem;
width: 100%;
height: 2.6vh;
margin-top:70%;
text-align: center;
align-items: center;
`
